INSERT INTO `menu` VALUES ('1', '美式咖啡', '995', '咖啡产品', 'https://www.starbucks.com.cn/images/products/caffe-americano.jpg', '24.00', '96', '1', '简单即是美味，萃取经典浓缩咖啡，以水调和，香气浓郁蔓溢。', '/coffee');
INSERT INTO `menu` VALUES ('2', '拿铁', '999', '咖啡产品', 'https://www.starbucks.com.cn/images/products/caffe-latte.jpg', '28.00', '0', '1', '本色的经典咖啡。新鲜萃取的浓缩咖啡中缓缓倒入蒸煮牛奶，覆上轻柔奶泡，简单的香与纯。', '/coffee');
INSERT INTO `menu` VALUES ('3', '卡布奇诺', '993', '咖啡产品', 'https://www.starbucks.com.cn/images/products/cappuccino.jpg', '28.00', '168', '1', '蒸煮牛奶与浓缩咖啡相融合，用丰厚绵密的奶泡覆顶，再撒上肉桂粉或可可粉。\r\n\r\n温馨提示：在奶泡消融前，请尽快享用。', '/coffee');
INSERT INTO `menu` VALUES ('4', '摩卡', '999', '咖啡产品', 'https://www.starbucks.com.cn/images/products/caffe-mocha.jpg', '30.00', '0', '1', '摩卡最初是指一种带有巧克力风味的咖啡豆，现在，这份巧克力咖啡仍然带给你纯正的温暖。在浓缩咖啡中加入摩卡酱，与蒸煮牛奶一起交织出丝滑与醇厚。', '/coffee');
INSERT INTO `menu` VALUES ('5', '馥芮白', '999', '咖啡产品', 'https://www.starbucks.com.cn/images/products/flat-white.jpg', '30.00', '0', '1', '融合绵密奶泡，风味更浓郁和甘甜。', '/coffee');
INSERT INTO `menu` VALUES ('6', '榛果风味拿铁', '996', '咖啡产品', 'https://www.starbucks.com.cn/images/products/hazelnut-flavored-latte.jpg', '30.00', '90', '1', '榛果风味与醇厚的浓缩咖啡相得益彰，融合在蒸煮牛奶中，带给你特别温暖的醇香。', '/coffee');
INSERT INTO `menu` VALUES ('7', '焦糖玛奇朵', '999', '咖啡产品', 'https://www.starbucks.com.cn/images/products/caramel-macchiato.jpg', '32.00', '0', '1', '玛奇朵在意大利语中的意思是“印记”。从蒸煮牛奶和添加风味糖浆开始，再倒入醇厚的浓缩咖啡，留下属于玛奇朵的独有印记。', '/coffee');
INSERT INTO `menu` VALUES ('8', '香草风味拿铁', '999', '咖啡产品', 'https://www.starbucks.com.cn/images/products/vanilla-flavored-latte.jpg', '30.00', '0', '1', '清雅香甜的香草风味糖浆让浓缩咖啡变得浪漫起来，开启拿铁的香草浪漫之旅。', '/coffee');
INSERT INTO `menu` VALUES ('9', '麦芽雪冷萃', '999', '咖啡产品', 'https://www.starbucks.com.cn/images/legacy/coffee-meets-ice-cream/cold-brew-malt.jpg', '40.00', '0', '1', '冷萃咖啡，双份新西兰进口香草冰淇淋\r\n巧克力味苦味酒，麦芽成分。', '/coffee');
INSERT INTO `menu` VALUES ('10', '冷萃浮乐朵', '999', '咖啡产品', 'https://www.starbucks.com.cn/images/legacy/coffee-meets-ice-cream/instore-nitro-cold-brew-float.jpg', '50.00', '0', '1', '冷萃咖啡，新西兰进口香草冰淇淋。', '/coffee');
INSERT INTO `menu` VALUES ('11', '冰摇黑加仑', '999', '饮料', 'https://www.starbucks.com.cn/images/products/blackcurrant-raspberry.jpg', '25.00', '0', '1', '黑加仑果汁融合醇香冰红茶，满满的甜蜜。', '/drink');
INSERT INTO `menu` VALUES ('12', '冷萃冰咖啡', '993', '饮料', 'https://www.starbucks.com.cn/images/products/cold-brew.jpg', '40.00', '240', '1', '10多个小时的慢速萃取，带出微甜的巧克力般风味，与特别的柑橘般风味，为你奉上沁甘顺滑的味觉感受。', '/drink');
INSERT INTO `menu` VALUES ('13', '抹茶星冰乐', '992', '饮料', 'https://www.starbucks.com.cn/images/products/green-tea-frappuccino.jpg', '35.00', '245', '1', '抹茶的清新，加一份牛奶的丝滑，与冰块和稀奶油搅拌出特有的清爽。', '/drink');
INSERT INTO `menu` VALUES ('14', '芒果果茶星冰乐', '949', '饮料', 'https://www.starbucks.com.cn/images/products/mango-passion-tea.jpg', '36.00', '1800', '1', '将花草茶的清爽，芒果西番莲果汁的酸甜与冰块融合。无需分清这一口是花香或果香，都是祛暑清凉的好饮品。', '/drink');
INSERT INTO `menu` VALUES ('15', '红梅黑加仑茶', '999', '饮料', 'https://www.starbucks.com.cn/images/products/blackcurrant-raspberry.jpg', '25.00', '0', '1', '红莓黑加仑果汁融合醇香冰红茶，两款风格各异的莓子与冰红茶摇出各自的果香，却又浑然一体，迷恋在莓与茶的酸甜中。', '/drink');
INSERT INTO `menu` VALUES ('16', '柚花草茶', '988', '饮料', 'https://www.starbucks.com.cn/images/products/citrus-grapefruit-herbal-tea.png', '24.00', '264', '1', '伴着柚片，酸味中还有着绿茶的清香，早起一杯，活力满满。', '/drink');
INSERT INTO `menu` VALUES ('17', '巧克力热饮', '999', '饮料', 'https://www.starbucks.com.cn/images/products/signature-chocolate-beverage.jpg', '22.00', '0', '1', '抵挡不住巧克力风味酱在蒸奶中飘散的浓郁可可香。只需在稀奶油上轻洒些许可可粉，就这样俘获你的心。', '/drink');
INSERT INTO `menu` VALUES ('18', '轻甜奶油冷萃', '999', '饮料', 'https://www.starbucks.com.cn/images/products/vanilla-flavor-sweet-cream-cold-brew.jpg', '45.00', '0', '1', '在冷萃冰咖啡的基础上，添加香草风味轻奶油。每份轻奶油都是根据西雅图的星巴克咖啡研发团队精心研发的配方新鲜手工调制而成， 使用脱脂奶也更符合现代人的健康生活理念。', '/drink');
INSERT INTO `menu` VALUES ('19', '梨光清润路芭茶', '999', '饮料', 'https://www.starbucks.com.cn/images/products/pear-rooibos-tea.png', '30.00', '0', '1', '路芭茶中梨果肉搅动着的芳香，口感清润，愉悦舒适。', '/drink');
INSERT INTO `menu` VALUES ('20', '冰摇芒果花草茶', '999', '饮料', 'https://www.starbucks.com.cn/images/products/iced-shaken-mango-herbal-juiced-tea.jpg', '30.00', '0', '1', '芒果与花草茶的碰撞，在凉爽中绽放。', '/drink');
INSERT INTO `menu` VALUES ('21', '蓝莓麦芬', '999', '美食', 'https://www.starbucks.com.cn/images/products/blueberry-muffin.jpg', '18.00', '0', '1', '享受进口大蓝莓果粒带来的曼妙滋味，顶层口感酥脆，内部松软。', '/food');
INSERT INTO `menu` VALUES ('22', '巧克力麦芬', '983', '美食', 'https://www.starbucks.com.cn/images/products/chocolate-muffin.jpg', '18.00', '288', '1', '享受高品质欧洲巧克力的细腻质感，巧克力风味浓郁醇正。', '/food');
INSERT INTO `menu` VALUES ('23', '法式焦糖酥', '999', '美食', 'https://www.starbucks.com.cn/images/products/french-caramel-bread.jpg', '16.00', '0', '1', '浓郁奶香的可颂面团，表层淋上香甜的焦糖沙司，一口咬下，外酥内柔，焦糖夹杂浓浓的奶香味在唇齿间蔓延，带给你美妙口感。', '/food');
INSERT INTO `menu` VALUES ('24', '榛子果仁酥', '997', '美食', 'https://www.starbucks.com.cn/images/products/hazelnut-croissant.jpg', '16.00', '32', '1', '层次分明，口感酥脆松软。', '/food');
INSERT INTO `menu` VALUES ('25', '蜂蜜提子司康', '999', '美食', 'https://www.starbucks.com.cn/images/products/honey-raisin-scone.jpg', '20.00', '0', '1', '提子干经朗姆酒精心炮制，具有独特口感', '/food');
INSERT INTO `menu` VALUES ('26', '红豆燕麦松饼', '999', '美食', 'https://www.starbucks.com.cn/images/products/red-bean-oats-scone.jpg', '18.00', '0', '1', '口感松软，醇厚的奶香味，再加入红豆沙和红豆，更增加了香甜的味道，同时加入燕麦片，更增加产品的价值。', '/food');
INSERT INTO `menu` VALUES ('27', '提子干松饼', '999', '美食', 'https://www.starbucks.com.cn/images/products/raisin-scone.jpg', '18.00', '0', '1', '口感松软，醇厚的奶香味，再加入香甜的提子干，使产品甜而不腻。', '/food');
INSERT INTO `menu` VALUES ('28', '核桃提子软面包', '999', '美食', 'https://www.starbucks.com.cn/images/products/raisins-walnut-bread.jpg', '16.00', '0', '1', '软法面包体，内含核桃、提子，添加不融芝士，奶香风味浓郁。', '/food');
INSERT INTO `menu` VALUES ('29', '蓝莓曲奇乳酪蛋糕', '999', '美食', 'https://www.starbucks.com.cn/images/products/blueberry-and-cookie-style-cheesecake.jpg', '24.00', '0', '1', '底部饼干底选择奥利奥风味曲奇制成，与众不同却不失经典的搭配，成为整只蛋糕的点睛之笔。配上大果粒蓝莓和树莓的清爽，这款轻盈爽滑的轻乳酪蛋糕更适合搭配有水果风味的饮品。', '/food');
INSERT INTO `menu` VALUES ('30', '法式马卡龙', '993', '美食', 'https://www.starbucks.com.cn/images/products/macaron.jpg', '20.00', '120', '1', '五种独特口味：焦糖玛奇朵、栗香拿铁、覆盆子、抹茶拿铁、香草', '/food');
INSERT INTO `menu` VALUES ('31', '咖啡提拉米苏', '999', '美食', 'https://www.starbucks.com.cn/images/products/starbucks-coffee-tiramisu.jpg', '24.00', '0', '1', '精选意大利进口马斯卡波尼芝士和玛莎拉白葡萄酒，与星巴克咖啡相融合，将芝士、酒和咖啡的独特风味融合于一身，相得益彰。', '/food');
INSERT INTO `menu` VALUES ('32', '法式闪电泡芙', '999', '美食', 'https://www.starbucks.com.cn/images/products/eclair.jpg', '22.00', '0', '1', '六种口味，丰富诱人：开心果、香草、柠檬、焦糖、覆盆子、巧克力', '/food');
INSERT INTO `menu` VALUES ('33', '芝士蛋糕', '999', '美食', 'https://www.starbucks.com.cn/images/products/new-york-style-creamy-cheesecake.jpg', '26.00', '0', '1', '清新独特的酸味奶油裱花，每一片都是独一无二的精致', '/food');
INSERT INTO `menu` VALUES ('34', '凯撒鸡肉卷', '999', '美食', 'https://www.starbucks.com.cn/images/products/caesar-chicken-wrap.jpg', '24.00', '0', '1', '凯撒鸡肉卷口味经典，风味浓厚。秘制烟熏鸡肉和培根打造双蛋白，同时搭配新鲜蔬菜馅料丰富十足。', '/food');
INSERT INTO `menu` VALUES ('35', '鸡肉芝香帕尼尼', '999', '美食', 'https://www.starbucks.com.cn/images/products/chicken-panini.jpg', '24.00', '0', '1', '松软可口的帕尼尼，精选优质香浓芝士，融合喷香鸡肉，让你口口满足。', '/food');
INSERT INTO `menu` VALUES ('36', '金枪鱼帕尼尼', '999', '美食', 'https://www.starbucks.com.cn/images/products/tuna-panini.jpg', '24.00', '0', '1', '松软可口的帕尼尼，包裹鲜香金枪鱼，搭配香浓芝士，微微加热，芝士融化，回味无穷。', '/food');
INSERT INTO `menu` VALUES ('37', '炒蛋菌菇虾仁卷', '999', '美食', 'https://www.starbucks.com.cn/images/products/eggs-mushroom-shrimp-wrap.jpg', '28.00', '0', '1', '优选嫩滑炒蛋以及秘制培根，搭配混合菌菇并点缀南美虾仁，丰富健康，一只早餐卷满足一天的营养需求。', '/food');
INSERT INTO `menu` VALUES ('38', '蜜汁培根蛋卷', '999', '美食', 'https://www.starbucks.com.cn/images/products/bacon-and-egg-wrap.jpg', '20.00', '0', '1', '蜜汁处理过的培根，搭配嫩滑炒鸡蛋和美味车达芝士，创新的用菠菜风味卷饼包裹一口下去，馥郁香气和丰富口感立刻充满口腔，令人回味无穷。', '/food');
INSERT INTO `menu` VALUES ('39', '牛肉芝士可颂', '999', '美食', 'https://www.starbucks.com.cn/images/products/beef-cheese-croissant.jpg', '18.00', '0', '1', '酥香可颂，搭配美味牛肉及芝士，口感上丰富的层次感，满足你挑剔的味蕾。', '/food');
INSERT INTO `menu` VALUES ('40', '火腿芝士可颂', '999', '美食', 'https://www.starbucks.com.cn/images/products/ham-and-cheese-croissant.jpg', '18.00', '0', '1', '优质进口黄油制作面团， 精心烤制的酥香可颂，内夹高品质芝士和火腿肉片，咸香适口，回味无穷。', '/food');
INSERT INTO `menu` VALUES ('41', '醋意桃桃', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/peach-shrub-fizz.jpg', '41.00', '0', '1', '大块黄桃+爽弹果冻，桃汁混搭桃醋风', '/star');
INSERT INTO `menu` VALUES ('42', '摩卡可可碎片星冰乐', '989', '季度精选', 'https://www.starbucks.com.cn/images/products/mocha-java-chip-frappuccino.jpg', '39.00', '390', '1', '摩卡酱和咖啡在冰块的碰撞下魅力四射，可可碎片带来不同的口感，顶部覆以稀奶油与摩卡酱，让浓郁变得更加有趣。(可可碎片为代可可脂巧克力)', '/star');
INSERT INTO `menu` VALUES ('43', '冰摇桃桃乌龙茶', '993', '季度精选', 'https://www.starbucks.com.cn/images/products/iced-shaken-peach-green-tea.jpg', '38.00', '228', '1', '内含原榨桃汁加上吃得到的大颗白桃果肉， 吃上一口，尝上一块，留住唇间的鲜醇甘厚。更有茶香留恋回味，给TA淘气的味蕾茶选。', '/star');
INSERT INTO `menu` VALUES ('44', '阿馥奇朵', '996', '季度精选', 'https://www.starbucks.com.cn/images/legacy/coffee-meets-ice-cream/affogato.jpg', '50.00', '150', '1', '双份精萃浓缩咖啡，新西兰进口香草冰淇淋', '/star');
INSERT INTO `menu` VALUES ('45', '冷萃浮乐朵', '991', '季度精选', 'https://www.starbucks.com.cn/images/legacy/coffee-meets-ice-cream/cold-brew-float.jpg', '40.00', '320', '1', '冷萃咖啡，新西兰进口香草冰淇淋', '/star');
INSERT INTO `menu` VALUES ('46', '酸柠浮冷萃', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/cold-brew-lemon-sour.jpg', '42.00', '0', '1', '灵感源自鸡尾酒威士忌酸，枫糖威士忌风味冷萃咖啡', '/star');
INSERT INTO `menu` VALUES ('47', '气炫冰山美式', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/sparkling-espresso-tonic.jpg', '30.00', '0', '1', '沁爽柠香揉合意式浓缩，低糖&低卡', '/star');
INSERT INTO `menu` VALUES ('48', '橙柚派对', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/triple-citrus.jpg', '40.00', '0', '1', '西柚+橙+柠檬三重果肉,富含维生素C', '/star');
INSERT INTO `menu` VALUES ('49', '火鸡培根麦芬', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/turkey-bacon-english-muffin.jpg', '18.00', '0', '1', '优质全麦面粉制作的英式麦芬，健康看得见。内夹香酥诱人的火鸡培根肉和质地细腻，口味柔和的白车达芝士，还有轻盈的纯蛋白饼。提供优质蛋白质的健康早餐选择。', '/star');
INSERT INTO `menu` VALUES ('50', '芝士火腿星明治', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/ham-and-gouda-sandwich.jpg', '22.00', '0', '1', '长时间的发酵，口感轻柔咸香', '/star');
INSERT INTO `menu` VALUES ('51', '火腿芝士恰巴特', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/slow-roasted-ham-cheese-ciabatta.jpg', '22.00', '0', '1', '5小时以上慢烤的意式熏煮火腿肉, 搭配香嫩鸡蛋饼以及轻柔咸香的高达芝士，放入外酥里软的恰巴特面包中，搭配一杯钟爱的咖啡，全新一天，从这款美味早餐开始吧！', '/star');
INSERT INTO `menu` VALUES ('52', '牛油果鸡肉培果', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/avocado-and-chicken-bagel.jpg', '28.00', '0', '1', '牛油果鸡肉焙果内含熏煮过的香嫩鸡胸肉，搭配美洲进口牛油果酱和车达芝士，口味清新健康', '/star');
INSERT INTO `menu` VALUES ('53', '英伦黄油饼干', '999', '季度精选', 'https://www.starbucks.com.cn/images/products/shortbread.png', '16.00', '0', '1', '富含黄油，有着浓郁绵密的奶香味，口感厚实浓郁酥脆。起源于苏格兰中世纪的英格兰甜点，当时稀有昂贵。', '/star');
